import UIKit

protocol LoginStoreProtocol {
  func deleteUsername()
  func deletePassword()
  func set(username: String)
  func set(password: String)
  func getUsername() -> String?
  func getPassword() -> String?
}

class LoginWorker {
  private let networkManager: Networkable
  private let dataStore: LoginStoreProtocol
  
  init(networkManager: Networkable = NetworkManager(), dataStore: LoginStoreProtocol = LoginKeychainStore()) {
    self.networkManager = networkManager
    self.dataStore = dataStore
  }
  
  func getLoginFieldsData(request: Login.TextFieldsData.Request) -> (username: String?, password: String?) {
    return (dataStore.getUsername(), dataStore.getPassword())
  }
  
  func login(request: Login.Request, completion: @escaping (Result<User, LoginError>) -> ()) {
    dataStore.set(username: request.username)
    dataStore.set(password: request.password)
    networkManager.login(request: request) { result in
      completion(result)
    }
  }
}
