import UIKit

class StatementsWorker {
  private let networkManager: Networkable
  
  init(networkManager: Networkable = NetworkManager()) {
    self.networkManager = networkManager
  }
  
  func fetchStatements(request: Statements.Data.Request, completion: @escaping (Result<[Statement], LoginError>) -> ()) {
    networkManager.fetchStatements(request: request) { result in
      completion(result)
    }
  }
}
